import * as React from 'react';
import { StyleSheet, Button } from 'react-native';
import { withSafeAreaInsets } from 'react-native-safe-area-context';
import Coupon_Card from '../components/Coupon_Card';
import { createStackNavigator } from '@react-navigation/stack';
import ProfilePage from '../screens/ProfilePage';
import { Text, View, ScrollView } from '../components/Themed';
import {useState} from "react";

export default function HomePage({ navigation }) {
  
  let [isFetch, setIsFetch] = useState(false);
  let [tableau, setTableau] = useState([]);

  if (tableau.length <= 0) {
    fetch('http://172.16.18.23:5000/customer/9CC0C6E1-5955-ABBB-E108-A5483D6DC689/coupons')
            .then(
                r => r.json()
            )
            .then(
                function (response) {
                    setIsFetch(true);
                    setTableau(response);
                    return;
                }
            );
  }
  console.log(tableau)

  let current_coupons_views = tableau.map(x => {
    return (
      <Coupon_Card key={x.id} coupon={x}/>
    );
  });

  return (
    <ScrollView style={styles.scroll} lightColor={true} lightColor="#eee" darkColor="rgba(255,255,255,0.1)">
      <Button
        onPress={() => navigation.navigate('ProfilePage')}
        title="Profil"
        color="#841584"
      />
      <View style={styles.container}>
        { current_coupons_views }
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  scroll: {
    display: 'flex',
    flex: 1,
    width: '100%',
  },
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    width: '100%',
  },
});

